        jButton1.setText("SUBMIT");
        jButton1.addActionListener(new java.awt.event.ActionListener() {
            public void actionPerformed(java.awt.event.ActionEvent evt) {
                jButton1ActionPerformed(evt);    
                try {
                    SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
                    String date=jTextField1.getText(); 
                    String s=jTextField2.getText();
                    String com=(date+" "+s);
                    Date parsedDate = dateFormat.parse(com);
                    String Date=dateFormat.format(parsedDate);
                String name=jTextField5.getText();
                int sayı=Integer.parseInt(jTextField3.getText());
                if (sayı>10)
                	{JOptionPane.showMessageDialog(jPanel1,"Table number need to be smaller than 10");}
                if(sayı<0)
                	{JOptionPane.showMessageDialog(jPanel1,"Table number need to be positive");}
                String phone=jTextField4.getText();
                if(phone.length()!=11)
                	{JOptionPane.showMessageDialog(jPanel1,"Phone number need to be 11 digits long.");}
                if(jTextField1.getText().isEmpty() || jTextField2.getText().isEmpty() || jTextField3.getText().isEmpty()|| jTextField4.getText().isEmpty() || jTextField5.getText().isEmpty()||jTextField6.getText().isEmpty()||jTextField7.getText().isEmpty()||jTextField8.getText().isEmpty())
                	{JOptionPane.showMessageDialog(jPanel1,"Please fill all fiels.");}
                String id=jTextField6.getText();
                String Surname =jTextField7.getText();
                int number=Integer.parseInt(jTextField8.getText());
                int w;
                if(sayı<=4)
                	w=1;
                else if(sayı<=8)
                	w=2;
                else w=3;
                Customer cus= new Customer(name,Surname,phone,number,id,sayı);
                cus.Makes_Reservation(Date, w);
                } catch (ParseException e) {
                    JOptionPane.showMessageDialog(jPanel1,"Invalid time or date format. Please enter time in HH:mm:ss and date in yyyy-MM-dd format.");
                    }
            }}
        );

