        jButton8.setText("Group by Customers with Client id");
        jButton8.addActionListener(new java.awt.event.ActionListener() {
            public void actionPerformed(java.awt.event.ActionEvent evt) {
            	try{  
            		StringBuilder resultStringBuilder = new StringBuilder();
        			Class.forName("com.mysql.cj.jdbc.Driver");  
        			Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/new_schema","root","ykaraman2002+2007");  
        			Statement st=con.createStatement();
        			ResultSet f=st.executeQuery("SELECT Client_id, Name, Surname\n"
        					+ "FROM Clients\n"
        					+ "GROUP BY Client_id\n"
        					+ "HAVING Client_id > 5;");
        			while (f.next()) {  
        				ResultSetMetaData f1= f.getMetaData();
        				int columnsNumber = f1.getColumnCount();
                        for (int i = 1; i <= columnsNumber; i++) {
                            resultStringBuilder.append(f.getString(i)).append(" \t");
                        }
                        resultStringBuilder.append("\n");
                    }
        			JOptionPane.showMessageDialog(jPanel1,resultStringBuilder.toString());
        		    }catch(Exception e){ System.out.println(e);}   
        }});

